import { styled } from 'solid-styled-components';
import leaflet from 'leaflet';

import { RestaurantType } from '../../types';
import { breakSmall } from '../../globalStyles';
import { onMount } from 'solid-js';

const Container = styled.div`
  border-top: 1px solid var(--gray6);
  border-bottom: 1px solid var(--gray6);
  min-height: 20rem;
  height: 20rem;
  margin: 1rem -1rem -1rem -1rem;
  overflow: hidden;

  @media (max-width: ${breakSmall}) {
    display: none;
  }
`;

const RestaurantPin = styled.div`
  display: flex;
  align-items: center;
`;

const RestaurantLabel = styled.span`
  font-weight: bold;
  text-shadow: -1px -1px 0 white, 1px -1px 0 white, -1px 1px 0 white,
    1px 1px 0 white;
`;

interface Props {
  restaurantPoint: [number, number];
  userPoint?: [number, number];
  restaurant: RestaurantType;
}

export default function RestaurantMap(props: Props) {
  let container: HTMLDivElement | undefined;

  onMount(() => {
    const map = leaflet.map(container!).setView(props.restaurantPoint, 14);
    leaflet.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19,
      attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
    }).addTo(map);
    leaflet.marker(props.restaurantPoint).addTo(map);
  });

  // {props.userPoint && (
  //   <Overlay anchor={props.userPoint} offset={[12, 12]}>
  //     <img
  //       width={24}
  //       height={24}
  //       src="data:image/png;base64,"
  //     />
  //   </Overlay>
  // )}

  return (
    <Container ref={container}>
    </Container>
  );
};
